import React, { useState } from 'react';

function Contacto() {
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [email, setEmail] = useState('');
  const [mensaje, setMensaje] = useState('');

  // Función para manejar el envío del formulario
  const handleSubmit = async(e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/enviar-formulario', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ nombre, apellido, email, mensaje }),
      });
  
      if (response.ok) {
        console.log('Formulario enviado con éxito');
      } else {
        console.error('Error al enviar el formulario');
      }
    } catch (error) {
      console.error('Error al enviar el formulario', error);
    }
  

    // Aquí puedes agregar la lógica para enviar los datos del formulario a tu correo electrónico o a través de una API
    // Puedes utilizar librerías como axios para hacer solicitudes HTTP

    // Luego de manejar el envío, puedes reiniciar los estados del formulario
    setNombre('');
    setApellido('');
    setEmail('');
    setMensaje('');
  };

  return (
    <div className="contacto-container">
      <h1>CONTACTAME</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="nombre">Nombre:</label>
        <input
          type="text"
          id="nombre"
          name="nombre"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          required
        />

        <label htmlFor="apellido">Apellido:</label>
        <input type="text" id="apellido" name="apellido" value={apellido} onChange={(e) => setApellido(e.target.value)} required />

        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        <label htmlFor="mensaje">Escribe un mensaje:</label>
        <textarea
          id="mensaje"
          name="mensaje"
          rows="4"
          value={mensaje}
          onChange={(e) => setMensaje(e.target.value)}
          required
        ></textarea>

        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};

export default Contacto;
